import React, {useEffect, useState} from "react";
import Axios from "axios";
import NewsItem from "./NewsItem";

const NewsList = () =>{
    const [articles, setArticles] = useState([]);
   useEffect(() => {
       const getArticles = async () => {
           const res = await Axios.get(
               "https://newsapi.org/v2/top-headlines?country=us&category=entertainment&pageSize=5&apiKey=c11c00d2a4f64b9381d31569344bb3fa"
           );
           setArticles(res.data.articles)
           console.log(res);
       };
       // setInterval(()=>
       //     {
       //         getArticles().then(res => console.log(res)).catch(err => console.error(err));
       //     }, 5000
       // )
       getArticles().then(res => console.log(res)).catch(err => console.error(err));

   }, []);
    return (
        <div>
            {articles.map(({title, description, url, urlToImage}) => (
                <NewsItem title={title} description={description} url={url} urlToImage={urlToImage}/>
            ))}
        </div>
    )
}

export default NewsList;

